package controller;import entity.User;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import service.TakePlane;import service.events.EmailService;/** * Created by hero on 17-4-2. */@Controllerpublic class EmailController {    @Autowired    private TakePlane takePlane;    @Autowired    private EmailService emailService;    @RequestMapping("/check")    public User receive() {        takePlane.check();        User user = new User();        user.setName("小明");        return user;    }    @RequestMapping("/send")    public void send() {        emailService.sendEmail("abc@123.com", "其实,你是个好人");        emailService.sendEmail("abc@123.com", "啊啊啊啊啊");        emailService.sendEmail("fjelwk@834.com", "hello world");    }}